CREATE MATERIALIZED VIEW default.log_entries_v3_mv TO default.writable_log_entries (`team_id` UInt64, `log_source` LowCardinality(String), `log_source_id` String, `instance_id` String, `timestamp` DateTime64(6, 'UTC'), `level` LowCardinality(String), `message` String, `_timestamp` Nullable(DateTime), `_offset` UInt64) AS SELECT team_id, log_source, log_source_id, instance_id, timestamp, level, message, _timestamp, _offset FROM default.kafka_log_entries_v3 WHERE toDate(timestamp) <= today()