CREATE TABLE default.sharded_ingestion_warnings (`team_id` Int64, `source` LowCardinality(String), `type` String, `details` String CODEC(ZSTD(3)), `timestamp` DateTime64(6, 'UTC'), `_timestamp` DateTime, `_offset` UInt64, `_partition` UInt64) ENGINE = ReplicatedMergeTree('/clickhouse/tables/{shard}/posthog.sharded_ingestion_warnings', '{replica}') PARTITION BY toYYYYMMDD(timestamp) ORDER BY (team_id, toHour(timestamp), type, source, timestamp) SETTINGS index_granularity = 8192