CREATE TABLE default.person_overrides (`team_id` Int32, `old_person_id` UUID, `override_person_id` UUID, `merged_at` DateTime64(6, 'UTC'), `oldest_event` DateTime64(6, 'UTC'), `created_at` DateTime64(6, 'UTC') DEFAULT now(), `version` Int32) ENGINE = ReplicatedReplacingMergeTree('/clickhouse/tables/noshard/posthog.person_overrides', '{replica}-{shard}', version) PARTITION BY toYYYYMM(oldest_event) ORDER BY (team_id, old_person_id) SETTINGS index_granularity = 8192