CREATE TABLE default.pg_embeddings (`domain` String, `team_id` Int64, `id` String, `vector` Array(Float32), `text` String, `properties` String CODEC(ZSTD(3)), `timestamp` DateTime64(6, 'UTC') DEFAULT now('UTC'), `is_deleted` UInt8) ENGINE = ReplicatedReplacingMergeTree('/clickhouse/tables/noshard/posthog.pg_embeddings', '{replica}-{shard}', timestamp, is_deleted) ORDER BY (team_id, domain, id) SETTINGS index_granularity = 512