CREATE TABLE default.plugin_log_entries (`id` UUID, `team_id` Int64, `plugin_id` Int64, `plugin_config_id` Int64, `timestamp` DateTime64(6, 'UTC'), `source` String, `type` String, `message` String, `instance_id` UUID, `_timestamp` DateTime, `_offset` UInt64) ENGINE = ReplicatedReplacingMergeTree('/clickhouse/tables/noshard/posthog.plugin_log_entries', '{replica}-{shard}', _timestamp) PARTITION BY toYYYYMMDD(timestamp) ORDER BY (team_id, plugin_id, plugin_config_id, timestamp) TTL toDate(timestamp) + toIntervalWeek(1) SETTINGS index_granularity = 512