CREATE TABLE default.property_definitions (`team_id` UInt32, `project_id` Nullable(UInt32), `name` String, `property_type` Nullable(String), `event` Nullable(String), `group_type_index` Nullable(UInt8), `type` UInt8 DEFAULT 1, `last_seen_at` DateTime, `version` UInt64 MATERIALIZED bitShiftLeft(toUInt64(NOT (property_type IS NULL)), 48) + toUInt64(toUnixTimestamp(last_seen_at))) ENGINE = ReplicatedReplacingMergeTree('/clickhouse/tables/noshard/posthog.property_definitions', '{replica}-{shard}', version) ORDER BY (team_id, type, coalesce(event, ''), name, coalesce(group_type_index, 255)) SETTINGS index_granularity = 8192