CREATE TABLE default.sharded_log_entries (`team_id` UInt64, `log_source` LowCardinality(String), `log_source_id` String, `instance_id` String, `timestamp` DateTime64(6, 'UTC'), `level` LowCardinality(String), `message` String, `_timestamp` DateTime, `_offset` UInt64) ENGINE = ReplicatedReplacingMergeTree('/clickhouse/tables/{shard}/posthog.sharded_log_entries', '{replica}', _timestamp) PARTITION BY toYYYYMMDD(timestamp) ORDER BY (team_id, log_source, log_source_id, instance_id, timestamp) TTL toDate(timestamp) + toIntervalDay(90) SETTINGS index_granularity = 1024, ttl_only_drop_parts = 1