CREATE VIEW default.custom_metrics (`name` String, `labels` Map(String, String), `value` Float64, `help` String, `type` String) AS SELECT * REPLACE (toFloat64(value) AS value) FROM default.custom_metrics_test UNION ALL SELECT * REPLACE (toFloat64(value) AS value) FROM default.custom_metrics_replication_queue UNION ALL SELECT * REPLACE (toFloat64(value) AS value) FROM default.custom_metrics_events_recent_lag UNION ALL SELECT * FROM default.custom_metrics_counters